
DROP TABLE IF EXISTS prodotti;
DROP TABLE IF EXISTS eventi;
DROP TABLE IF EXISTS prenotazioni;
DROP TABLE IF EXISTS commenti;
DROP TABLE IF EXISTS admin;

CREATE TABLE prodotti(
	idprodotto INT(6) PRIMARY KEY NOT NULL auto_increment,	
	titolo VARCHAR(100) NOT NULL,			
	tipoalc VARCHAR(64) NOT NULL,			
	tipologia VARCHAR(64) NOT NULL,			
	gradazione INT(3) NOT NULL, 			
	regione VARCHAR(60) NOT NULL, 			
	temp VARCHAR(15) NOT NULL,				
	aroma VARCHAR(100) NOT NULL, 			
	desc_prod VARCHAR(2000) NOT NULL,
	alt VARCHAR(200) DEFAULT NULL,
	immagini LONGBLOB DEFAULT NULL,
	INDEX(titolo)
);

CREATE TABLE eventi (
	idevento INT(11) PRIMARY KEY NOT NULL auto_increment,
	evento VARCHAR(255) NOT NULL,	
	desc_evento VARCHAR(3000) NOT NULL,			
	dataZ DATE NOT NULL,							
	posti INT(4) NOT NULL,
	alt VARCHAR(200) DEFAULT NULL,
	imgeventi LONGBLOB DEFAULT NULL,						
	INDEX(dataz)										
);


CREATE TABLE prenotazioni (
	idprenotazione INT(11) PRIMARY KEY NOT NULL auto_increment,
	dataevento DATE NOT NULL,
	nominativo VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
	nposti INT(6) NOT NULL
);

CREATE TABLE commenti (
	nominativo VARCHAR(255) NOT NULL,
	email VARCHAR(255) PRIMARY KEY NOT NULL,
	oggetto VARCHAR(3000) NOT NULL,
	datacommento DATE NOT NULL
	);

CREATE TABLE admin (				
    username VARCHAR(50) NOT NULL UNIQUE,
    passwordz VARCHAR(64) NOT NULL,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);







